import React, { useState } from 'react';
import './App.css';

function App() {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const handleAddBook = (book) => {
    setBooks([...books, book]);
  };

  const handleSearch = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const filteredBooks = books.filter((book) => {
    const categoryCondition = selectedCategory === 'All' || book.category === selectedCategory;
    const searchCondition = book.title.toLowerCase().includes(searchQuery.toLowerCase());
    return categoryCondition && searchCondition;
  });

  return (
    <div className="App">
      <h1>Book Library App</h1>
      <div>
        <input
          type="text"
          placeholder="Search by title..."
          value={searchQuery}
          onChange={handleSearch}
        />
        <select value={selectedCategory} onChange={handleCategoryChange}>
          <option value="All">All</option>
          <option value="Fiction">Fiction</option>
          <option value="Non-Fiction">Non-Fiction</option>
        </select>
      </div>
      <div className="book-list">
        {filteredBooks.map((book, index) => (
          <div key={index} className="book">
            <h2>{book.title}</h2>
            <p>Author: {book.author}</p>
            <p>Category: {book.category}</p>
          </div>
        ))}
      </div>
      <div className="add-book-form">
        <h2>Add a New Book</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            const title = e.target.title.value;
            const author = e.target.author.value;
            const category = e.target.category.value;
            handleAddBook({ title, author, category });
            e.target.reset();
          }}
        >
          <input type="text" name="title" placeholder="Title" />
          <input type="text" name="author" placeholder="Author" />
          <select name="category">
            <option value="Fiction">Fiction</option>
            <option value="Non-Fiction">Non-Fiction</option>
          </select>
          <button type="submit">Add Book</button>
        </form>
      </div>
    </div>
  );
}

export default App;
