import React, { useState } from 'react';
import BookCard from './BookCard';

const Home = () => {
  const [library, setLibrary] = useState([]);
  const books = [
    {
      id: 1,
      title: 'Book 1',
      author: 'V',
      publicationDate: '2022-01-01',
      coverImage: 'https://rukminim2.flixcart.com/image/312/312/k4ehnrk0/regionalbooks/m/4/7/letus-c-authentic-guide-to-c-programming-language-original-imafnbh4gkzs8hnm.jpeg?q=70', // Replace with actual cover image URL
    },
    {
      id: 2,
      title: 'Book 2',
      author: 'Author 2',
      publicationDate: '2022-02-15',
      coverImage: 'https://rukminim2.flixcart.com/image/312/312/kq18n0w0/book/e/y/b/advance-c-programming-original-imag44agzjptxbpw.jpeg?q=70', // Replace with actual cover image URL
    },
    {
      id: 3,
      title: 'Book 2',
      author: 'Author 2',
      publicationDate: '2022-02-15',
      coverImage: '', // Replace with actual cover image URL
    },
    {
      id: 4,
      title: 'Book 2',
      author: 'Author 2',
      publicationDate: '2022-02-15',
      coverImage: '', // Replace with actual cover image URL
    },
    {
      id: 4,
      title: 'Book 2',
      author: 'Author 2',
      publicationDate: '2022-02-15',
      coverImage: '', // Replace with actual cover image URL
    },{
      id: 4,
      title: 'Book 2',
      author: 'Author 2',
      publicationDate: '2022-02-15',
      coverImage: '', // Replace with actual cover image URL
    },
  ];

  const addToLibrary = (book) => {
    setLibrary([...library, book]);
  };

  return (
    <div className="container mt-4">
      <h1>Library</h1>
      <div className="card-group">
        {books.map((book) => (
          <div className="col-md-4 mb-4" key={book.id}>
            <BookCard book={book} addToLibrary={addToLibrary} />
          </div>
        ))}
      </div>
      <h2>Your Library</h2>
      <ul>
        {library.map((book) => (
          <li key={book.id}>{book.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
