import React, { useState } from 'react';
// import BookCard from './BookCard';

const Home = () => {
  const [library, setLibrary] = useState([]);
  const books = [
    {
      id: 1,
      title: 'Programming in C',
      author: 'Kochan Stephen G.',
      publicationDate: '2022-01-01',
      coverImage: 'https://rukminim2.flixcart.com/image/416/416/kjiwfbk0-0/book/m/f/o/programming-in-c-original-imafzfztyhhygycr.jpeg?q=70',
    },
    {
      id: 2,
      title: 'Book 2',
      author: 'Author 2',
      publicationDate: '2022-02-15',
      coverImage: 'https://rukminim2.flixcart.com/image/416/416/kiew3gw0/book/w/y/z/let-us-c-authentic-guide-to-c-programming-language-17th-edition-original-imafy7gnctuggpgv.jpeg?q=70'},
    // Add more book objects as needed
  ];

  const addToLibrary = (book) => {
    setLibrary([...library, book]);
  };

  return (
    <div className="container mt-4">
      <h1>Library</h1>
      <div className="row">
        {books.map((book) => (
          <div className="col-md-4" key={book.id}>
            <BookCard book={book} addBook={addToLibrary} />
          </div>
        ))}
      </div>
      <h2>Your Library</h2>
      <ul>
        {library.map((book) => (
          <li key={book.id}>{book.title}</li>
        ))}
      </ul>
    </div>
  );
};

export default Home;
