import React, { useState } from 'react';
import './Library.css';

const Library = () => {
  const [books, setBooks] = useState([]);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [publicationDate, setPublicationDate] = useState('');
  const [coverImage, setCoverImage] = useState('');
  const [editIndex, setEditIndex] = useState(null);
  const [selectedGenre, setSelectedGenre] = useState('');
  const [searchTerm, setSearchTerm] = useState('');

  const handleTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const handleAuthorChange = (event) => {
    setAuthor(event.target.value);
  };

  const handleGenreChange = (event) => {
    setGenre(event.target.value);
  };

  const handlePublicationDateChange = (event) => {
    setPublicationDate(event.target.value);
  };

  const handleCoverImageChange = (event) => {
    setCoverImage(event.target.value);
  };

  const addBook = () => {
    if (title.trim() !== '') {
      const newBook = {
        title,
        author,
        genre,
        publicationDate,
        coverImage,
      };
      setBooks([...books, newBook]);
      setTitle('');
      setAuthor('');
      setGenre('');
      setPublicationDate('');
      setCoverImage('');
    }
  };

  const editBook = (index) => {
    const bookToEdit = books[index];
    setTitle(bookToEdit.title);
    setAuthor(bookToEdit.author);
    setGenre(bookToEdit.genre);
    setPublicationDate(bookToEdit.publicationDate);
    setCoverImage(bookToEdit.coverImage);
    setEditIndex(index);
  };

  const updateBook = () => {
    if (editIndex !== null) {
      const updatedBooks = [...books];
      updatedBooks[editIndex] = {
        title,
        author,
        genre,
        publicationDate,
        coverImage,
      };
      setBooks(updatedBooks);
      setEditIndex(null);
      setTitle('');
      setAuthor('');
      setGenre('');
      setPublicationDate('');
      setCoverImage('');
    }
  };

  const cancelEdit = () => {
    setEditIndex(null);
    setTitle('');
    setAuthor('');
    setGenre('');
    setPublicationDate('');
    setCoverImage('');
  };

  const deleteBook = (index) => {
    const updatedBooks = books.filter((_, i) => i !== index);
    setBooks(updatedBooks);
    setEditIndex(null);
  };

  return (
    <div className="library-container">
      <h1>Library Collection</h1>
      <div className="input-container">
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={handleTitleChange}
        />
        <input
          type="text"
          placeholder="Author"
          value={author}
          onChange={handleAuthorChange}
        />
        <input
          type="text"
          placeholder="Genre"
          value={genre}
          onChange={handleGenreChange}
        />
        <input
          type="text"
          placeholder="Publication Date"
          value={publicationDate}
          onChange={handlePublicationDateChange}
        />
        <input
          type="text"
          placeholder="Cover Image URL"
          value={coverImage}
          onChange={handleCoverImageChange}
        />
        <select
          value={selectedGenre}
          onChange={(event) => setSelectedGenre(event.target.value)}
        >
          <option value="">All Genres</option>
          <option value="Science Fiction">Science Fiction</option>
          <option value="Fantasy">Fantasy</option>
          <option value="Mystery">Mystery</option>
          {/* Add more genres as needed */}
        </select>
        <input
          type="text"
          placeholder="Search by Title, Author, or Genre"
          value={searchTerm}
          onChange={(event) => setSearchTerm(event.target.value)}
        />
        {editIndex !== null ? (
          <div className="action-buttons">
            <button className="update-button" onClick={updateBook}>Update</button>
            <button className="cancel-button" onClick={cancelEdit}>Cancel</button>
          </div>
        ) : (
          <button className="add-button" onClick={addBook}>Add Book</button>
        )}
      </div>
      <ul className="book-list">
        {books
          .filter((book) => {
            const searchLower = searchTerm.toLowerCase();
            return (
              book.title.toLowerCase().includes(searchLower) ||
              book.author.toLowerCase().includes(searchLower) ||
              book.genre.toLowerCase().includes(searchLower)
            );
          })
          .map((book, index) => (
            <li key={index} className="book-item">
              <div className="book-details">
                <img src={book.coverImage} alt={book.title} className="book-cover" />
                <div>
                  <h3>{book.title}</h3>
                  <p>Author: {book.author}</p>
                  <p>Genre: {book.genre}</p>
                  <p>Publication Date: {book.publicationDate}</p>
                </div>
              </div>
              {editIndex === index ? (
                <div className="action-buttons">
                  <button className="edit-button" onClick={() => editBook(index)}>Edit</button>
                  <button className="delete-button" onClick={() => deleteBook(index)}>Delete</button>
                </div>
              ) : (
                <div className="action-buttons">
                  <button className="edit-button" onClick={() => editBook(index)}>Edit</button>
                </div>
              )}
            </li>
          ))}
      </ul>
    </div>
  );
};

export default Library;
