import React, { useState } from 'react';
import BookCard from './BookCard';
import SearchBar from './SearchBar'; // Make sure to adjust the path based on your project structure


  
 
const Home = () => {
 
  const [searchResults, setSearchResults] = useState([]);
 
  const handleSearch = (searchTerm) => {
    const filteredBooks = books.filter((book) =>
      book.title.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setSearchResults(filteredBooks);
  };

  const displayBooks = searchResults.length > 0 ? searchResults : books;

  const [library, setLibrary] = useState([]);
  

  const addToLibrary = (book) => {
    setLibrary([...library, book]);
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center">Library</h1>
      <div className="row row-cols-1 row-cols-md-2 row-cols-lg-3 g-4">
        {books.map((book) => (
          <div className="col" key={book.id}>
            <BookCard book={book} addToLibrary={addToLibrary} />
          </div>
        ))}
      </div>
      
    </div>
  );
};

export default Home;
