import React, { useState } from 'react';
import './Library.css';

const Library = () => {
  const [books, setBooks] = useState([]);
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [genre, setGenre] = useState('');
  const [publicationDate, setPublicationDate] = useState('');
  const [coverImage, setCoverImage] = useState('');
  const [editIndex, setEditIndex] = useState(null);

  const addBook = () => {
    if (title.trim() !== '') {
      const newBook = {
        title,
        author,
        genre,
        publicationDate,
        coverImage,
      };
      setBooks([...books, newBook]);
      setTitle('');
      setAuthor('');
      setGenre('');
      setPublicationDate('');
      setCoverImage('');
    }
  };

  const editBook = (index) => {
    const bookToEdit = books[index];
    setTitle(bookToEdit.title);
    setAuthor(bookToEdit.author);
    setGenre(bookToEdit.genre);
    setPublicationDate(bookToEdit.publicationDate);
    setCoverImage(bookToEdit.coverImage);
    setEditIndex(index);
  };

  const updateBook = () => {
    if (editIndex !== null) {
      const updatedBooks = [...books];
      updatedBooks[editIndex] = {
        title,
        author,
        genre,
        publicationDate,
        coverImage,
      };
      setBooks(updatedBooks);
      setEditIndex(null);
      setTitle('');
      setAuthor('');
      setGenre('');
      setPublicationDate('');
      setCoverImage('');
    }
  };

  const cancelEdit = () => {
    setEditIndex(null);
    setTitle('');
    setAuthor('');
    setGenre('');
    setPublicationDate('');
    setCoverImage('');
  };

  const deleteBook = (index) => {
    const updatedBooks = books.filter((_, i) => i !== index);
    setBooks(updatedBooks);
    setEditIndex(null);
  };

  return (
    <div className="library-container">
      <h1>Library Collection</h1>
      <div className="input-container">
        {/* ... (input fields and buttons) */}
      </div>
      <ul className="book-list">
        {books.map((book, index) => (
          <li key={index} className="book-item">
            <div className="book-details">
              <img src={book.coverImage} alt={book.title} className="book-cover" />
              <div>
                <h3>{book.title}</h3>
                <p>Author: {book.author}</p>
                <p>Genre: {book.genre}</p>
                <p>Publication Date: {book.publicationDate}</p>
              </div>
            </div>
            {editIndex === index ? (
              <div className="book-actions">
                <button className="action-button update-button" onClick={updateBook}>Update</button>
                <button className="action-button cancel-button" onClick={cancelEdit}>Cancel</button>
                <button className="action-button delete-button" onClick={() => deleteBook(index)}>Delete</button>
              </div>
            ) : (
              <div className="book-actions">
                <button className="action-button edit-button" onClick={() => editBook(index)}>Edit</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Library;
