import React, { useState, useEffect } from 'react';

function BookFilter() {
  const [books, setBooks] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [titleFilter, setTitleFilter] = useState('');
  const [authorFilter, setAuthorFilter] = useState('');
  const [genreFilter, setGenreFilter] = useState('');

  useEffect(() => {
    // Simulate fetching books from an API
    const mockBooks = [
      { id: 1, title: 'Book 1', author: 'Author 1', genre: 'Fiction' },
      { id: 2, title: 'Book 2', author: 'Author 2', genre: 'Fantasy' },
      { id: 3, title: 'Book 3', author: 'Author 1', genre: 'Sci-Fi' },
      { id: 4, title: 'Book 4', author: 'Author 3', genre: 'Mystery' },
    ];
    setBooks(mockBooks);
    setFilteredBooks(mockBooks);
  }, []);

  useEffect(() => {
    // Filter books based on title, author, and genre
    const filtered = books.filter((book) =>
      book.title.toLowerCase().includes(titleFilter.toLowerCase()) &&
      book.author.toLowerCase().includes(authorFilter.toLowerCase()) &&
      book.genre.toLowerCase().includes(genreFilter.toLowerCase())
    );
    setFilteredBooks(filtered);
  }, [titleFilter, authorFilter, genreFilter, books]);

  return (
    <div>
      <h1>Book Filter</h1>
      <input
        type="text"
        placeholder="Filter by Title"
        value={titleFilter}
        onChange={(e) => setTitleFilter(e.target.value)}
      />
      <input
        type="text"
        placeholder="Filter by Author"
        value={authorFilter}
        onChange={(e) => setAuthorFilter(e.target.value)}
      />
      <input
        type="text"
        placeholder="Filter by Genre"
        value={genreFilter}
        onChange={(e) => setGenreFilter(e.target.value)}
      />
      <ul>
        {filteredBooks.map((book) => (
          <li key={book.id}>
            <strong>{book.title}</strong> by {book.author} (Genre: {book.genre})
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BookFilter;
