// BookCard.js

import React from 'react';
import Library from './Component/Library';
const BookCard = ({ book, addBook }) => {
  return (
    <div className="card" style={{ width: '18rem' }}>
      <img src={book.coverImage} className="card-img-top" alt={book.title} />
      <div className="card-body">
        <h5 className="card-title">{book.title}</h5>
        <p className="card-text">Author: {book.author}</p>
        <p className="card-text">Publication Date: {book.publicationDate}</p>
        <button className="btn btn-primary" onClick={() => addBook(book)}>
          Add to Library
        </button>
      </div>
    </div>
  );
};

export default BookCard;
