import React, { useState } from 'react';
import './Library.css'; // Import the CSS file for styling

const Library = () => {
 

  return (
    <div className="library-container">
      <h1>Library Collection</h1>
      <div className="input-container">
        {/* ... (input fields and buttons) */}
      </div>
      <ul className="book-list">
        {books.map((book, index) => (
          <li key={index} className="book-item">
            <div className="book-details">
              <img src={book.coverImage} alt={book.title} className="book-cover" />
              <div>
                <h3>{book.title}</h3>
                <p>Author: {book.author}</p>
                <p>Genre: {book.genre}</p>
                <p>Publication Date: {book.publicationDate}</p>
              </div>
            </div>
            {editIndex === index ? (
              <div className="book-actions">
                <button className="action-button update-button" onClick={updateBook}>Update</button>
                <button className="action-button cancel-button" onClick={cancelEdit}>Cancel</button>
                <button className="action-button delete-button" onClick={() => deleteBook(index)}>Delete</button>
              </div>
            ) : (
              <div className="book-actions">
                <button className="action-button edit-button" onClick={() => editBook(index)}>Edit</button>
              </div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Library;
